import React, {useState} from 'react';
import { EmptyState, Layout, Page } from '@shopify/polaris';
import {ResourcePicker} from '@shopify/polaris';
import store from 'store-js';



function index() {

  const [modal, setModal] = useState({open : false});
  const emptyState = !store.get('ids');

  handleSection = (resource) => {
    const idsFormResource = resource.selection.map((product) => product.id);
    setModal({open: false});
    store.set('ids', idsFormResource)
  }

  return(
    <Page>
      <Layout>
        <EmptyState
          heading="Quotation management"
          action={{
            content: 'Add quotation',
            onAction: () => {console.log('click')}
          }}
          secondaryAction={{content: 'Learn more', url: 'https://help.shopify.com'}}
          image="https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg"
        >

        </EmptyState>
      </Layout>
    </Page>
  )
}

export default index;
